<?php
$name = $steam = $epic = $contact = $ps = $xbox = '';
// Check connection
if ($_SERVER["REQUEST_METHOD"] == "POST") {

    if (empty(trim($_POST["name"]))) {
        $error = "Zadej název hráče";
    } else {
        $result = Db::queryAll("SELECT ID FROM players WHERE NAME = ?", trim($_POST["name"]));
        if (count($result) == 1) {
            $error = "Hráč již existuje";
        } else {
            if (Db::query(
                "INSERT INTO players (NAME, CONTACT, STEAM, EPIC, PS, XBOX) VALUES (?,?,?,?,?,?)",
                trim($_POST["name"]),
                trim($_POST["contact"]),
                trim($_POST["steam"]),
                trim($_POST["epic"]),
                trim($_POST["ps"]),
                trim($_POST["xbox"])
            ) == 1) {
                $newPlayer = "Hráč vytvořen";
            }
        }
    }
}
